import streams from '../api/streams';
import * as actionType from './action.types';

export const signIn = (userId) => {
    return {
        type: actionType.SIGN_IN,
        payload: {
            userId
        }
    };
};

export const signOut = () => {
    return {
        type: actionType.SIGN_OUT
    };
};

export const createStream = (formValues) => {
    return async (dispatch) => {
        const res = await streams.post('/streams', formValues);
        dispatch({
            type: actionType.CREATE_STREAM,
            payload: res.data
        })
    };
};

export const fetchStreams = () => {
    return async (dispatch) => {
        const res = await streams.get('/streams');
        dispatch({
            type: actionType.FETCH_STREAMS,
            payload: res.data
        })
    };
};

export const fetchStream = (id) => {
    return async (dispatch) => {
        const res = await streams.get(`/stream/${id}`);
        dispatch({
            type: actionType.FETCH_STREAM,
            payload: res.data
        })
    };
};

export const editStream = (id, formValues) => {
    return async (dispatch) => {
        const res = await streams.put(`/streams/${id}`, formValues);
        dispatch({
            type: actionType.EDIT_STREAM,
            payload: data
        })
    };
};
