import React from 'react';
import { Field, reduxForm } from 'redux-form';

class StreamCreate extends React.Component {
    renderInput({ input, label }) {
        return (
            <div className="field">
                <label>{label}</label>
                <input
                    onChange={input.onChange}
                    value={input.value}
                />
            </div>
        );
    }

    render() {
        return (
            <form className="ui form">
                <Field component={this.renderInput} label="Enter Title" name="title" />
                <Field component={this.renderInput} label="Enter Description" name="description" />
            </form>
        );
    }
}

export default reduxForm({
    form: 'streamCreate'
})(StreamCreate);
