import * as actionTypes from '../actions/action.types';

const INTIAL_STATE = {
    isSignedIn: null
}

export default (state = INTIAL_STATE, action) => {
    switch(action.type) {
        case actionTypes.SIGN_IN:
            return { ...state, isSignedIn:true };
        case actionTypes.SIGN_OUT:
            return { ...state, isSignedIn:false };
        default:
            return state;
    }
}
